import React, { useState } from "react";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";
import { Component } from "react";
import axios from 'axios';

export default class CreateArea extends Component {
  constructor(props){
    super(props);
    this.state = {
      isExpanded: false,
      title: "",
      content: ""
    };
  }

  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };


  handleSubmit = e => {
    e.preventDefault();

    const { title, content } = this.state;

    const note = {
      title,
      content
    };

    axios
      .post('http://localhost:5000/notes', note)
      .then(() => console.log('Note Created'))
      .catch(err => {
        console.error(err);
      });
  };


  expand() {
    setExpanded(true);
  }

  render() {
    return (
      <div>
        <form className="create-note">
          {isExpanded && (
            <input
              name="title"
              onChange={this.handleInputChange}
              value={note.title}
              placeholder="Title"
            />
          )}
          <textarea
            name="content"
            onClick={expand}
            onChange={this.handleInputChange}
            value={note.content}
            placeholder="Take a note..."
            rows={isExpanded ? 3 : 1}
          />
          <Zoom in={isExpanded}>
            <Fab onClick={this.handleSubmit}>
              <AddIcon />
            </Fab>
          </Zoom>
        </form>
      </div>
    );
  }
}
